const BASE_URL = "http://localhost:8000/api"; // Replace with your backend API base URL

export const deleteFile = async (userName, fileNames, setDeleteStatus) => {
  try {
    const response = await fetch(`${BASE_URL}/delete-files/`, {
      method: "DELETE",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        user_name: userName,
        files_to_delete: fileNames,
      }),
    });

    const data = await response.json();
    console.log("Delete successful:", data.message);
    setDeleteStatus("success");
  } catch (error) {
    console.error("Delete failed:", error);
    setDeleteStatus("fail");
  }
};
