import React, { useState } from "react";

const MultipleFileUploader = () => {
  const [files, setFiles] = (useState < FileList) | (null > null);
  const [status, setStatus] =
    (useState < "initial") | "uploading" | "success" | ("fail" > "initial");

  const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    if (e.target.files) {
      setStatus("initial");
      setFiles(e.target.files);
    }
  };

  const handleUpload = async () => {
    if (files) {
      setStatus("uploading");

      const formData = new FormData();
      Array.from(files).forEach((file) => {
        formData.append("files", file);
      });

      try {
        const response = await fetch("https://httpbin.org/post", {
          method: "POST",
          body: formData,
        });

        if (response.ok) {
          setStatus("success");
        } else {
          setStatus("fail");
        }
      } catch (error) {
        console.error("Error uploading files:", error);
        setStatus("fail");
      }
    }
  };

  return (
    <div className="container mt-5">
      <div className="row justify-content-center">
        <div className="col-md-8">
          <div className="card">
            <div className="card-header bg-primary text-white text-center">
              <h1>Multiple File Uploader</h1>
            </div>
            <div className="card-body">
              <div className="form-group">
                <label htmlFor="file" className="sr-only">
                  Choose files
                </label>
                <input
                  id="file"
                  type="file"
                  multiple
                  onChange={handleFileChange}
                  className="form-control-file"
                />
              </div>
              {files && (
                <div>
                  {Array.from(files).map((file, index) => (
                    <div key={index} className="mb-3">
                      <h5>File {index + 1} Details:</h5>
                      <p>Name: {file.name}</p>
                      <p>Type: {file.type}</p>
                      <p>Size: {file.size} bytes</p>
                    </div>
                  ))}
                </div>
              )}
              <button
                onClick={handleUpload}
                className="btn btn-primary"
                disabled={!files || status === "uploading"}
              >
                {status === "uploading" ? "Uploading..." : "Upload Files"}
              </button>
              {status === "success" && (
                <p className="text-success mt-3">✅ Uploaded successfully!</p>
              )}
              {status === "fail" && (
                <p className="text-danger mt-3">❌ Upload failed!</p>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default MultipleFileUploader;
