// api.js

const BASE_URL = "http://localhost:8000/api"; // Replace with your backend API base URL

export const handleUpload = async (userName, files, setStatus, setFiles) => {
  setStatus("uploading");

  const formData = new FormData();
  formData.append("user_name", userName);
  Array.from(files).forEach((file) => {
    formData.append("file", file);
  });

  try {
    const response = await fetch("http://localhost:8000/api/files/", {
      method: "POST",
      body: formData,
    });

    const data = await response.json(); // Parse response body as JSON

    if (response.ok) {
      setStatus("success");
      setFiles(null); // Clear files after successful upload
    } else {
      setStatus("fail");
      console.error("Upload failed:", data.message);
    }
  } catch (error) {
    console.error(error);
    setStatus("fail");
  }
};

export const updateFiles = async (formData) => {
  try {
    const response = await fetch(`${BASE_URL}/files/`, {
      method: "update",
      body: formData,
    });
    return response;
  } catch (error) {
    console.error("Failed to upload files:", error);
    throw error; // Handle error in calling component
  }
};

export const getFileList = async () => {
  try {
    const response = await fetch(`${BASE_URL}/files/`);
    const data = await response.json();
    return data;
  } catch (error) {
    console.error("Failed to fetch file list:", error);
    throw error; // Handle error in calling component
  }
};

export const deleteFile = async (fileId) => {
  try {
    const response = await fetch(`${BASE_URL}/files/${fileId}`, {
      method: "DELETE",
    });
    return response;
  } catch (error) {
    console.error("Failed to delete file:", error);
    throw error; // Handle error in calling component
  }
};
