import React, { useState } from "react";

const FileUpload = () => {
  const [username, setUsername] = useState("");
  const [selectedFile, setSelectedFile] = useState(null);
  const [uploadedFiles, setUploadedFiles] = useState([]);
  const [message, setMessage] = useState("");

  const handleUsernameChange = (event) => {
    setUsername(event.target.value);
  };

  const handleFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
  };

  const handleFileUpload = () => {
    if (!username || !selectedFile) {
      setMessage("Please enter a username and select a file.");
      return;
    }

    // Simulate file upload by adding it to the state
    setUploadedFiles([
      ...uploadedFiles,
      { username, fileName: selectedFile.name },
    ]);
    setMessage("File uploaded successfully.");
    setUsername("");
    setSelectedFile(null);
  };

  return (
    <div className="container mt-5">
      <h1 className="text-center mb-4">File Upload</h1>
      <div className="card p-4">
        <div className="form-group">
          <label htmlFor="username">Username:</label>
          <input
            type="text"
            className="form-control"
            id="username"
            value={username}
            onChange={handleUsernameChange}
          />
        </div>
        <div className="form-group mt-3">
          <label htmlFor="file">Select File:</label>
          <input
            type="file"
            className="form-control"
            id="file"
            onChange={handleFileChange}
          />
        </div>
        <button className="btn btn-primary mt-3" onClick={handleFileUpload}>
          Upload
        </button>
        {message && <div className="alert alert-info mt-3">{message}</div>}
      </div>
      <h2 className="text-center mt-5">Uploaded Files</h2>
      <table className="table table-striped mt-3">
        <thead>
          <tr>
            <th>Username</th>
            <th>File Name</th>
          </tr>
        </thead>
        <tbody>
          {uploadedFiles.map((file, index) => (
            <tr key={index}>
              <td>{file.username}</td>
              <td>{file.fileName}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default FileUpload;
