import React, { useState } from "react";
import {
  handleUpload,
  updateFiles,
  getFileList,
  deleteFile,
} from "../services/api";

const MultipleFileUploader = () => {
  const [userName, setUserName] = useState("");
  const [files, setFiles] = useState(null);
  const [status, setStatus] = useState("initial");
  const [updateStatus, setUpdateStatus] = useState("initial");
  const [getFileListStatus, setGetFileListStatus] = useState("initial");
  const [deleteStatus, setDeleteStatus] = useState("initial");
  const [filesToDelete, setFilesToDelete] = useState("");
  const [fileList, setFileList] = useState([]);

  const handleFileChange = (e) => {
    if (e.target.files) {
      setStatus("initial");
      setFiles(e.target.files);
    }
  };

  const handleDeleteFiles = () => {
    const fileNames = filesToDelete.split(",").map((name) => name.trim());
    deleteFile(fileNames, setDeleteStatus);
  };

  return (
    <div className="container mt-5">
      <div className="row justify-content-center">
        <div className="col-md-8">
          <div className="card">
            <div className="card-header bg-primary text-white text-center">
              <h1>Multiple File Uploader</h1>
            </div>
            <div className="card-body">
              <div className="form-group d-flex align-items-center">
                <label htmlFor="username" className="col-sm-2 mb-0">
                  <div className="mb-2">User Name:</div>
                </label>
                <div className="col-sm-10">
                  <input
                    type="text"
                    id="username"
                    value={userName}
                    onChange={(e) => setUserName(e.target.value)}
                    className="form-control"
                    placeholder="Enter your name"
                    style={{ flex: 1, maxWidth: "300px" }}
                  />
                </div>
              </div>

              <div className="form-group">
                <label
                  htmlFor="file"
                  className="sr-only"
                  style={{ marginRight: "10px" }}
                >
                  <div className="mb-2">Choose files</div>
                </label>
                <input
                  id="file"
                  type="file"
                  multiple
                  onChange={handleFileChange}
                  className="form-control-file"
                  style={{ fontSize: "1rem" }}
                />
              </div>
              <div className="form-group">
                {files && (
                  <div>
                    {Array.from(files).map((file, index) => (
                      <div key={index} className="mb-3">
                        <h5>File {index + 1} Details:</h5>
                        <p>Name: {file.name}</p>
                        <p>Type: {file.type}</p>
                        <p>Size: {file.size} bytes</p>
                      </div>
                    ))}
                  </div>
                )}
              </div>
              <div className="form-group">
                <div className="mb-2">
                  Upload Files
                  <button
                    onClick={() =>
                      handleUpload(userName, files, setStatus, setFiles)
                    }
                    className="btn btn-primary mr-3"
                    disabled={!files || status === "uploading"}
                    style={{ margin: "0 10px", fontSize: "1rem" }}
                  >
                    {status === "uploading" ? "Uploading..." : "Upload Files"}
                  </button>
                </div>
              </div>
              {status === "success" && (
                <p className="text-success mt-2">✅ Uploaded successfully!</p>
              )}
              {status === "fail" && (
                <p className="text-danger mt-2">❌ Upload failed!</p>
              )}
              <div className="form-group">
                <div className="mb-2">
                  Update Files
                  <button
                    onClick={() =>
                      updateFiles(userName, files, setUpdateStatus, setFiles)
                    }
                    className="btn btn-info"
                    disabled={!files}
                    style={{ margin: "0 10px", fontSize: "1rem" }}
                  >
                    {updateStatus === "updating"
                      ? "Updating..."
                      : "Update Files"}
                  </button>
                </div>
              </div>
              {updateStatus === "success" && (
                <p className="text-success mt-2">✅ Updated successfully!</p>
              )}
              {updateStatus === "fail" && (
                <p className="text-danger mt-2">❌ Updated failed!</p>
              )}

              <div className="mb-2">
                Get Files
                <button
                  onClick={() =>
                    getFileList(userName, files, setGetFileListStatus, setFiles)
                  }
                  className="btn btn-secondary"
                  style={{ margin: "0 10px", fontSize: "1rem" }}
                >
                  Get File List
                </button>
              </div>
              {getFileListStatus === "success" && (
                <div className="mt-2">
                  <h5>File List:</h5>
                  <ul>
                    {fileList.map((file, index) => (
                      <li key={index}>{file.name}</li>
                    ))}
                  </ul>
                </div>
              )}
              {getFileListStatus === "fail" && (
                <p className="text-danger mt-2">❌ Failed to get file list!</p>
              )}
              <div>
                <div>
                  <label htmlFor="filesToDelete" className="mb-2">
                    Enter file names to delete (comma separated):
                  </label>
                  <input
                    type="text"
                    id="filesToDelete"
                    value={filesToDelete}
                    onChange={(e) => setFilesToDelete(e.target.value)}
                    className="form-control"
                    placeholder="file1.txt, file2.jpg"
                  />
                </div>
                <button
                  onClick={handleDeleteFiles}
                  className="btn btn-danger mt-2"
                  disabled={!filesToDelete}
                  style={{ margin: "0 10px", fontSize: "1rem" }}
                >
                  Delete Files
                </button>
              </div>
              {deleteStatus === "success" && (
                <p className="text-success mt-2">✅ Deleted successfully!</p>
              )}
              {deleteStatus === "fail" && (
                <p className="text-danger mt-2">❌ Delete failed!</p>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default MultipleFileUploader;
