import React, { useState } from "react";
import { updateFiles, getFileList, deleteFile } from "../services/api";

const MultipleFileUploader = () => {
  const [userName, setUserName] = useState("");
  const [files, setFiles] = useState(null);
    const [status, setStatus] = useState("initial");
    const [updateStatus, setupdateStatus] = useState("initial");
    const [getFileListStatus, setgetFileListStatus] = useState("initial");
    const [deleteStatus, setdeleteStatus] = useState("initial");

  const handleFileChange = (e) => {
    if (e.target.files) {
      setStatus("initial");
      setFiles(e.target.files);
    }
  };

  const handleUpload = async () => {
    if (files && userName) {
      setStatus("uploading");

      const formData = new FormData();
      formData.append("user_name", userName);
      Array.from(files).forEach((file) => {
        formData.append("file", file);
      });

      try {
        const response = await fetch("http://localhost:8000/api/files/", {
          method: "POST",
          body: formData,
        });

        const data = await response.json(); // Parse response body as JSON

        if (response.ok) {
          setStatus("success");
          setFiles(null); // Clear files after successful upload
        } else {
          setStatus("fail");
          console.error("Upload failed:", data.message);
        }
      } catch (error) {
        console.error(error);
        setStatus("fail");
      }
    }
  };

  return (
    <div className="container mt-5">
      <div className="row justify-content-center">
        <div className="col-md-8">
          <div className="card">
            <div className="card-header bg-primary text-white text-center">
              <h1>Multiple File Uploader</h1>
            </div>
            <div className="card-body">
              <div className="form-group d-flex align-items-center">
                <label htmlFor="username" className="col-sm-2 mb-0">
                  <div className="mb-2">User Name:</div>
                </label>
                <div className="col-sm-10">
                  <input
                    type="text"
                    id="username"
                    value={userName}
                    onChange={(e) => setUserName(e.target.value)}
                    className="form-control"
                    placeholder="Enter your name"
                    style={{ flex: 1, maxWidth: "300px" }}
                  />
                </div>
              </div>

              <div className="form-group">
                <label
                  htmlFor="file"
                  className="sr-only"
                  style={{ marginRight: "10px" }}
                >
                  <div className="mb-2">Choose files</div>
                </label>
                <input
                  id="file"
                  type="file"
                  multiple
                  onChange={handleFileChange}
                  className="form-control-file"
                  style={{ fontSize: "1rem" }}
                />
              </div>
              <div className="form-group">
                {files && (
                  <div>
                    {Array.from(files).map((file, index) => (
                      <div key={index} className="mb-3">
                        <h5>File {index + 1} Details:</h5>
                        <p>Name: {file.name}</p>
                        <p>Type: {file.type}</p>
                        <p>Size: {file.size} bytes</p>
                      </div>
                    ))}
                  </div>
                )}
              </div>
              <div className="form-group">
                <div className="mb-2">
                  Upload Files
                  <button
                    onClick={handleUpload}
                    className="btn btn-primary mr-3"
                    disabled={!files || status === "uploading"}
                    style={{ margin: "0 10px", fontSize: "1rem" }}
                  >
                    {status === "uploading" ? "Uploading..." : "Upload Files"}
                  </button>
                </div>
                {status === "success" && (
                  <p className="text-success mt-2">✅ Uploaded successfully!</p>
                )}
                {status === "fail" && (
                  <p className="text-danger mt-2">❌ Upload failed!</p>
                )}
                <div className="form-group">
                  <div className="mb-2">
                    Update Files
                    <button
                      onClick={updateFiles}
                      className="btn btn-info"
                      disabled={!files}
                      style={{ margin: "0 10px", fontSize: "1rem" }}
                    >
                      {updateStatus === "updating"
                        ? "Updating..."
                        : "Update Files"}
                    </button>
                  </div>
                  {updateStatus === "success" && (
                    <p className="text-success mt-2">
                      ✅ Updated successfully!
                    </p>
                  )}
                  {updateStatus === "fail" && (
                    <p className="text-danger mt-2">❌ Updated failed!</p>
                  )}

                  <div className="mb-2">
                    Get Files
                    <button
                      onClick={getFileList}
                      className="btn btn-secondary"
                      style={{ margin: "0 10px", fontSize: "1rem" }}
                    >
                      {/* {status === "Get File List" ? "Uploading..." : "Upload Files"} */}
                      Get File List
                    </button>
                  </div>
                  <div>
                    Delete Files
                    <button
                      onClick={deleteFile}
                      className="btn btn-danger"
                      disabled={!files}
                      style={{ margin: "0 10px", fontSize: "1rem" }}
                    >
                      Delete File
                    </button>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default MultipleFileUploader;
