import React, { useState } from "react";

const MultipleFileUploader = () => {
  const [userName, setUserName] = useState("");
  const [files, setFiles] = useState(null);
  const [status, setStatus] = useState("initial");

  const handleFileChange = (e) => {
    if (e.target.files) {
      setStatus("initial");
      setFiles(e.target.files);
    }
  };

  const handleUpload = async () => {
    if (files) {
      setStatus("uploading");

      // Simulate file upload delay
      setTimeout(() => {
        setStatus("success");
        setFiles(null); // Clear files after successful "upload"
      }, 2000); // Simulate 2 second delay (adjust as needed)
    }
  };

  return (
    <div className="container mt-5">
      <div className="row justify-content-center">
        <div className="col-md-8">
          <div className="card">
            <div className="card-header bg-primary text-white text-center">
              <h1>Multiple File Uploader</h1>
            </div>
            <div className="card-body">
              <div className="form-group">
                <label htmlFor="username">User Name:</label>
                <input
                  type="text"
                  id="username"
                  value={userName}
                  onChange={(e) => setUserName(e.target.value)}
                  className="form-control"
                  placeholder="Enter your name"
                />
              </div>
              <div className="form-group">
                <label htmlFor="file" className="sr-only">
                  Choose files
                </label>
                <input
                  id="file"
                  type="file"
                  multiple
                  onChange={handleFileChange}
                  className="form-control-file"
                />
              </div>
              <div className="form-group">
                {files && (
                  <div>
                    {Array.from(files).map((file, index) => (
                      <div key={index} className="mb-3">
                        <h5>File {index + 1} Details:</h5>
                        <p>Name: {file.name}</p>
                        <p>Type: {file.type}</p>
                        <p>Size: {file.size} bytes</p>
                      </div>
                    ))}
                  </div>
                )}
              </div>
              <div className="form-group">
                <button
                  onClick={handleUpload}
                  className="btn btn-primary mr-3"
                  disabled={!files || status === "uploading"}
                >
                  {status === "uploading" ? "Uploading..." : "Upload Files"}
                </button>
                {status === "success" && (
                  <p className="text-success mt-2">✅ Uploaded successfully!</p>
                )}
                {status === "fail" && (
                  <p className="text-danger mt-2">❌ Upload failed!</p>
                )}
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default MultipleFileUploader;
