// api.js

const BASE_URL = "http://localhost:8000/api"; // Replace with your backend API base URL

export const handleUpload = async (userName, files, setStatus, setFiles) => {
  if (files && userName) {
    setStatus("uploading");

    const formData = new FormData();
    formData.append("user_name", userName);
    Array.from(files).forEach((file) => {
      formData.append("file", file);
    });
    try {
      const response = await fetch("http://localhost:8000/api/files/", {
        method: "POST",
        body: formData,
      });

      const data = await response.json(); // Parse response body as JSON

      if (response.ok) {
        setStatus("success");
        setFiles(null); // Clear files after successful upload
      } else {
        setStatus("fail");
        console.error("Upload failed:", data.message);
      }
    } catch (error) {
      console.error(error);
      setStatus("fail");
    }
  }
};

export const updateFiles = async (userName, file, setStatus, setFiles) => {
  if (file && userName) {
    setStatus("updating");

    const formData = new FormData();
    formData.append("user_name", userName);
    Array.from(file).forEach((file) => {
      formData.append("file", file);
    });
    try {
      const response = await fetch(`http://localhost:8000/api/files/${file}/`, {
        method: "PUT",
        body: formData,
      });

      const data = await response.json(); // Parse response body as JSON

      if (response.ok) {
        setStatus("success");
        setFiles(null); // Clear files after successful upload
      } else {
        setStatus("fail");
        console.error("Upload failed:", data.message);
      }
    } catch (error) {
      console.error(error);
      setStatus("fail");
    }
  }
};

export const getFileList = async (
  userName,
  files,
  setgetFileListStatus,
  setFiles
) => {
  try {
    const response = await fetch(`${BASE_URL}/files-list/`, {
      method: "GET",
    });
    const data = await response.json();
    setFiles(data.files);
    setgetFileListStatus("success");
  } catch (error) {
    console.error("Failed to fetch file list:", error);
    setgetFileListStatus("fail");
  }
  //   try {
  //     const response = await fetch(`${BASE_URL}/files-list/`);
